         public void setNombre(String nbre) {
        nombre.setText(nbre);
    }

    public void setApellido(String a) {
        apellido.setText(a);
    }

    public void setCargo(String cargo) {
        this.cargo.setSelectedItem(cargo);
    }

    public void setGenero(String g) {
        if (g.equals("Femenino")) {
            femenino.setSelected(true);
        } else {
            masculino.setSelected(true);
        }
    }

    public void setDias(int d) {
        dias.setValue(d);
    }
    
    public void setSalario(String s){
        salario.setText(s);
    }
    
    public void setIngresos(String ing){
        ingresos.setText(ing);
    }
    
    public void setPagos(String pag){
        pagos.setText(pag);
    }
    
    public void setAportes(String aport){
        aportes.setText(aport);
    }

