    private void fuenteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_fuenteActionPerformed
       Font f = new Font((String) fuente.getSelectedItem(),txtArea.getFont().getStyle(),txtArea.getFont().getSize());
        txtArea.setFont(f);
    }//GEN-LAST:event_fuenteActionPerformed

    private void estiloActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_estiloActionPerformed
        Font f = new Font(txtArea.getFont().getName(),estilo.getSelectedIndex(),txtArea.getFont().getSize());
        txtArea.setFont(f);
    }//GEN-LAST:event_estiloActionPerformed

    private void tamanoStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_tamanoStateChanged
        Font f = new Font(txtArea.getFont().getName(),estilo.getSelectedIndex(),(int)tamano.getValue());
        txtArea.setFont(f);
    }//GEN-LAST:event_tamanoStateChanged

